import React, { useState, useEffect } from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "../css/login.css";
import Modal from "./Modal"; // Adjust the import path if necessary

export default function AuthPage() {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isResponsive, setIsResponsive] = useState(window.innerWidth <= 768);
  const sliderRef = React.useRef(null);

  useEffect(() => {
    function handleResize() {
      setIsResponsive(window.innerWidth <= 768);
    }
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  function handleSubmit(event) {
    event.preventDefault();
    setIsModalOpen(true);
  }

  function handleModalClose() {
    setIsModalOpen(false);
  }

  const settings = {
    dots: true,
    infinite: false,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    arrows: false,
    swipe: isResponsive,
  };

  return (
    <div className="App">
      <Slider ref={sliderRef} {...settings}>
        <div className="purple-section">
          <img
            src="images/purplevideo.gif"
            height="100%"
            width="100%"
            alt="purple"
          />
          {isResponsive && (
            <button className="toggle-button" onClick={() => sliderRef.current.slickNext()}>
              Login
            </button>
          )}
        </div>
        <div className="white-section">
          <div className="top-bar">
            <h1>CineLens</h1>
            <p>Hey there, welcome back!</p>
            <div className="signin-buttons">
              <button>
                <img src="images/google.svg" alt="Google Logo" /> Sign in with
                Google
              </button>
              <button>
                <img src="images/facebook.svg" alt="Facebook Logo" /> Sign in with
                Facebook
              </button>
            </div>
          </div>
          <div className="container">
            <div className="line" />
            <div className="text">Or Login With</div>
            <div className="line" />
          </div>
          <form className="login-form" onSubmit={handleSubmit}>
            <label>
              Username
              <input
                type="text"
                name="username"
                placeholder="Enter Your Username"
              />
            </label>

            <label>
              Password
              <input
                type="password"
                name="password"
                placeholder="Enter Your Password"
              />
            </label>

            <div className="button-container">
              <button type="submit">Login</button>
            </div>
            <p>
              {" "}
              Don't Have An Account? <a href="./RegisterPage">Register</a>
            </p>
          </form>
          {isResponsive && (
            <button className="toggle-button" onClick={() => sliderRef.current.slickPrev()}>
              Back
            </button>
          )}
        </div>
      </Slider>

      <Modal show={isModalOpen} onClose={handleModalClose} />
    </div>
  );
}
